CREATE TABLE USER(ID INT PRIMARY KEY AUTO_INCREMENT, FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), DOB DATE, USERNAME VARCHAR(255), PASSWORD VARCHAR(255), EMAIL VARCHAR(255), ROLE VARCHAR(6), BALANCE DECIMAL);
CREATE TABLE MANUFACTURER(ID INT PRIMARY KEY AUTO_INCREMENT, VALUE VARCHAR(255));
CREATE TABLE MODEL (ID INT PRIMARY KEY AUTO_INCREMENT, VALUE VARCHAR(255));
CREATE TABLE COLOR (ID INT PRIMARY KEY AUTO_INCREMENT, VALUE_EN VARCHAR(20), VALUE_RU VARCHAR(20));
CREATE TABLE VEHICLE(ID INT PRIMARY KEY AUTO_INCREMENT, VEHICLE_TYPE VARCHAR(5), MODEL_ID INT, MANUFACTURER_ID INT, PRODUCTION_YEAR INT, COLOR_ID INT, MILEAGE DECIMAL, FUEL_TYPE VARCHAR(11), OPERABLE BOOLEAN, RENT_PRICE DECIMAL, DRIVER_ID INT, STANDING_PLACES_NUMBER INT, PASSENGER_SEATS_NUMBER INT, DOORS_NUMBER INT, CONDITIONER BOOLEAN, MAX_PAYLOAD DECIMAL, ENCLOSED BOOLEAN, TIPPER BOOLEAN);
ALTER TABLE VEHICLE ADD FOREIGN KEY (DRIVER_ID) REFERENCES USER (ID);
ALTER TABLE VEHICLE ADD FOREIGN KEY (MODEL_ID) REFERENCES MODEL(ID);
ALTER TABLE VEHICLE ADD FOREIGN KEY (MANUFACTURER_ID) REFERENCES MANUFACTURER(ID);
ALTER TABLE VEHICLE ADD FOREIGN KEY (COLOR_ID) REFERENCES COLOR(ID);
CREATE TABLE VH_ORDER (ID INT PRIMARY KEY AUTO_INCREMENT, CLIENT_ID INT, DATE_START DATE, DAYS_COUNT INT, DATE_ORDERED DATE, SUM INT);
ALTER TABLE VH_ORDER ADD FOREIGN KEY (CLIENT_ID) REFERENCES USER(ID);
ALTER TABLE VH_ORDER ADD FOREIGN KEY (VEHICLE_ID) REFERENCES VEHICLE(ID);